function isDate(obj){
    if(isNaN(Date.parse(obj))) return false; else return true;
};

module.exports.isDate=isDate;


module.exports.dateAdd=function(dateType,intNum,strDate){
    if(isDate(strDate))
    {
        const sdate = new Date(strDate);
        //const sdate =strDate;
        switch(dateType.toLowerCase())
        {
            case "day":
                sdate.setDate(sdate.getDate()+intNum);
                break;
            case "week":
                sdate.setDate(sdate.getDate()+(intNum*7));
                break;
            case "fortnight":
                sdate.setDate(sdate.getDate()+(intNum*15));
                break;
            case "month":
                sdate.setMonth(sdate.getMonth()+intNum);
                break;
            case "year":
                sdate.setFullYear(sdate.getFullYear()+intNum);
                break;
        }

        let month=sdate.getMonth()+1;
        let day=sdate.getDate();
        let hours=sdate.getUTCHours();
        let minutes=sdate.getUTCMinutes();
        let seconds=sdate.getUTCSeconds();
        let milliSeconds=sdate.getUTCMilliseconds();
    
        if(month<10){ month= '0'+month;}
        if(day<10){day='0'+day;}
        if(hours<10){hours='0'+hours;}
        if(minutes<10){minutes='0'+minutes;}
        if(seconds<10){seconds='0'+seconds;}
        if(milliSeconds<10){milliSeconds='0'+milliSeconds;}

        return sdate;
    }
    else
    {
        return strDate;
    }
}